<?phpif ($_SERVER['REQUEST_METHOD'] === 'POST') {    // Get the data from the AJAX request and sanitize if needed    $username = filter_var($_POST['username'], FILTER_SANITIZE_STRING);    $useremail = filter_var($_POST['useremail'], FILTER_VALIDATE_EMAIL);    $userphone = filter_var($_POST['userphone'], FILTER_SANITIZE_STRING);    $useraddress = filter_var($_POST['useraddress'], FILTER_SANITIZE_STRING);    $propertylisted = filter_var($_POST['propertylisted'], FILTER_SANITIZE_STRING);    $propertycondition = filter_var($_POST['propertycondition'], FILTER_SANITIZE_STRING);    $closedate = filter_var($_POST['closedate'], FILTER_SANITIZE_STRING);    $estimatedhomevalue = filter_var($_POST['estimatedhomevalue'], FILTER_SANITIZE_STRING);    $subject = filter_var($_POST['subject'], FILTER_SANITIZE_STRING);    $from = filter_var($_POST['from'], FILTER_VALIDATE_EMAIL);    $to = filter_var($_POST['to'], FILTER_VALIDATE_EMAIL);}// Initialize the response array$response = array();$emailContent = "Name: $username <br> Email: $useremail<br>Phone: $userphone<br>Address: $useraddress<br>Property Listed:$propertylisted<br>Property Condition: $propertycondition<br>Close Date:$closedate<br>Estimated Home Value:$estimatedhomevalue";// $emailContent = "Name: $username <br> Email: $useremail<br>Phone: $userphone<br>Address: $useraddress";// Validate email addressesif ($from && $to) {                $headers = "MIME-Version: 1.0" . "\r\n";                $headers .= "Content-type: text/html; charset=iso-8859-1" . "\r\n";                    $headers .= "From: $from\r\n";                $headers .= "Reply-To: $from\r\n";                $headers .= "Return-Path: $from\r\n";                $headers .= "CC: $to\r\n";                $headers .= "BCC: $to\r\n"."X-Mailer: PHP/" . phpversion();                    if (mail($to, $subject, $emailContent, $headers)) {        $response = array(            'message' => "Hello, $username! Thank you for submitting your details.",            'data' => 'We will get in touch soon!',        );    } else {        $response = array(            'status' => 'error',            'message' => "Mail sending failed.",            'data' => 'Please try again later.',        );    }} else {    $response = array(        'status' => 'error',        'message' => 'Invalid email addresses provided.',        'data' => 'Please provide valid email addresses.',    );}// Return the response as JSONheader('Content-Type: application/json');echo json_encode($response);?>